mapper.update:
update item_cat
set name      = #{name,jdbcType=VARCHAR},
    status    = #{status,jdbcType=INTEGER},
    menuClick = #{menuClick,jdbcType=VARCHAR}
where cid = #{cid,jdbcType=INTEGER}

mapper.select:
select cid, name, status, menuClick
from item_cat
where cid = #{cid,jdbcType=INTEGER}

select cid, name, status, menuClick
from item_cat
where status = #{status}
